mapper.insert:
INSERT INTO recipe (recipe_title, recipe_content, user_nickname, insert_date, view_count, good_count, recipe_filename, user_num, comment_count) 
SELECT #{recipe_title}, #{recipe_content}, user_nickname, #{insert_date}, #{view_count}, #{good_count}, #{recipe_filename}, user_num, #{comment_count}
FROM users
WHERE user_nickname = #{user_nickname}
